export default (location) => {

  var lang = 'cz',
      currency = 'Kč'
  if(location.split('/')[1] === 'en'){
    lang = 'en';
    currency = '€';
  }else{
    lang = 'cz';
    currency = 'Kč';
  }

  return {lang, currency}
}
