import { useRouter } from 'next/router'

export default () => {

  const router = useRouter()

  var lang = 'cz',
      currency = 'Kč'
  if(router.pathname.split('/')[1] === 'en'){
    lang = 'en';
    currency = '€';
  }else{
    lang = 'cz';
    currency = 'Kč';
  }

  return {lang, currency}
}
